function initSlick() {
    $('.tarefas').slick({
      dots: true,
      infinite: true,
      slidesToShow: 1,
      adaptiveHeight: true,
      autoplaySpeed: 2000,
      responsive: [
        {
          breakpoint: 1440, // Tamanho da tela em pixel
          settings: 'unslick' // Remover as configurações responsivas acima desse breakpoint
        },
        {
          breakpoint: 702, // Tamanho da tela em pixel
          settings: {
            slidesToShow: 1 // Quantidade de slides a serem exibidos quando a tela for igual ou menor que 702px
          }
        }
      ]
    });
  }
  
  // Função para reconfigurar o Slick quando ocorrer uma alteração no tamanho da janela
  function handleWindowResize() {
    // Destruir o Slick existente
    $('.tarefas').slick('unslick');
    // Inicializar novamente o Slick
    initSlick();
  }
  
  // Inicializar o Slick
  initSlick();
  
  // Adicionar um ouvinte de evento 'resize' ao objeto window
  window.addEventListener('resize', handleWindowResize);
  